server.listen(1000, () => {
  console.log('Serveur demarré en écoute sur le porte 1000 !');
  mongoose.connect('mongodb://localhost/smartphone');
  mongoose.connection
    .once('open', () => console.log('Connexion à MongoDB établie !'))
    .on('error', error => {
      console.warn('Warning', error);
    });
});
